let currentQuestionIndex = 0;

function renderCurrentQuestion() {
  const container = document.getElementById("questions-container");
  container.innerHTML = "";

  const q = selectedQuestions[currentQuestionIndex];
  const questionElement = document.createElement("div");
  questionElement.innerHTML = `<p>${currentQuestionIndex + 1}. ${q.question}</p>`;

  q.options.forEach((option, optionIndex) => {
    const input = document.createElement("input");
    input.type = "radio";
    input.name = `question${currentQuestionIndex}`;
    input.value = option;
    input.addEventListener("change", () => {
      userAnswers[currentQuestionIndex] = option;
    });

    if (userAnswers[currentQuestionIndex] === option) {
      input.checked = true;
    }

    const label = document.createElement("label");
    label.innerHTML = option;

    questionElement.appendChild(input);
    questionElement.appendChild(label);
  });

  container.appendChild(questionElement);
}

function showNextQuestion() {
  // Verifica se o usuário marcou uma alternativa antes de passar para a próxima pergunta
  const currentOptions = document.getElementsByName(`question${currentQuestionIndex}`);
  const isChecked = Array.from(currentOptions).some(option => option.checked);

  if (!isChecked) {
    alert("Por favor, marque uma alternativa antes de passar para a próxima pergunta.");
    return; // Impede a transição se nenhuma opção foi marcada
  }

  currentQuestionIndex++;

  if (currentQuestionIndex < selectedQuestions.length) {
    renderCurrentQuestion();
  } else {
    // Se não houver mais perguntas, finalizar o exame
    finishExam();
  }
}


document.getElementById("nextButton").addEventListener("click", showNextQuestion);

function showPreviousQuestion() {
  if (currentQuestionIndex > 0) {
    currentQuestionIndex--;
    renderCurrentQuestion();
  }
  document.getElementById("previousButton").addEventListener("click", showPreviousQuestion);
}




// Iniciar o timer e renderizar a primeira pergunta quando a página carrega
startTimer(2 * 60 * 60);
renderCurrentQuestion();
document.getElementById("questions-container").style.display = "block"; // Mostrar a primeira pergunta
    

function finishExam() {
  // Parar o cronômetro
  clearInterval(timerInterval);

  // Realizar a correção aqui
  const correctAnswers = selectedQuestions.map(q => q.correctAnswer);
  const results = [];

  for (let i = 0; i < userAnswers.length; i++) {
    const userAnswer = userAnswers[i];
    const correctAnswer = correctAnswers[i];
    const isCorrect = userAnswer === correctAnswer;
    results.push({ question: selectedQuestions[i].question, userAnswer, correctAnswer, isCorrect });
  }

  // Mostrar os resultados na tela
  showResults(results);
}

function showResults(results) {
 const container = document.getElementById("background");
 container.innerHTML = ""; // Limpa o conteúdo existente

  // Adiciona um cabeçalho
container.classList.add("text-result");
container.innerHTML = "<h2>Clique sobre a questão desejada e confira o gabarito</h2>";




  // Cria um contêiner para os resultados
  const resultsContainer = document.createElement("div");
  resultsContainer.classList.add("result-container"); // Adiciona uma classe para estilização

  results.forEach((result, index) => {
    // Cria uma div para cada resultado
    const resultDiv = document.createElement("div");
    resultDiv.classList.add("result-item"); // Adiciona uma classe para estilização

    // Adiciona o número da questão
    const numberDiv = document.createElement("div");
    numberDiv.textContent = index + 1;
    numberDiv.classList.add("number-cell"); // Adiciona a classe number-cell

    // Adiciona marcação de acerto ou erro
    if (result.isCorrect) {
      numberDiv.classList.add("correct-cell");
    } else {
      numberDiv.classList.add("incorrect-cell");
    }

    // Adiciona um evento de clique à div da questão
    numberDiv.addEventListener("click", function () {
      showDetails(result.question, result.userAnswer, result.correctAnswer);
    });

    // Adiciona o número da questão à div do resultado
    resultDiv.appendChild(numberDiv);

    // Adiciona a div do resultado ao contêiner de resultados
    resultsContainer.appendChild(resultDiv);
  });

  // Adiciona o contêiner de resultados à div "background"
  container.appendChild(resultsContainer);

  // Cria um contêiner para os detalhes da questão
  const detailsContainer = document.createElement("div");
  detailsContainer.id = "details-container";
  container.appendChild(detailsContainer);

  // Adiciona um botão para refazer a prova
  const backButton = document.createElement("button");
  backButton.textContent = "Refazer Prova";
  backButton.onclick = function () {
    window.location.href = '../index.php';
  };

  // Adiciona o botão à div "background"
  container.appendChild(backButton);

 function showDetails(question, userAnswer, correctAnswer) {
    // Obtém o contêiner de detalhes
    const detailsContainer = document.getElementById("details-container");

    // Limpa o conteúdo existente no contêiner de detalhes
    detailsContainer.innerHTML = "";

    // Adiciona os detalhes da questão abaixo da tabela
    detailsContainer.innerHTML += `<p>Pergunta: ${question}</p>`;

    // Verifica se a resposta do usuário está correta
    const isCorrect = userAnswer === correctAnswer;

    // Adiciona os detalhes da resposta do usuário
    if (isCorrect) {
        detailsContainer.innerHTML += `<p>Sua Resposta: <span style="color: green;">${userAnswer}</span></p>`;
    } else {
        detailsContainer.innerHTML += `<p>Sua Resposta: <span style="color: red;">${userAnswer}</span></p>`;
        detailsContainer.innerHTML += `<p >Resposta Correta: <span style="color: green;">${correctAnswer}</span></p>`;
    }
    
  }

}




